package model.metier;

import java.io.File;
import java.io.IOException;
import java.util.ArrayList;

import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;
import javax.xml.parsers.ParserConfigurationException;

import org.w3c.dom.Document;
import org.w3c.dom.Element;
import org.w3c.dom.Node;
import org.w3c.dom.NodeList;
import org.xml.sax.SAXException;



public class LireXML
{
	/*
	 * Récupération d'une instance de la classe "DocumentBuilderFactory" en vue de creer un parseur.<br>
	 * Parseur : analyseur syntaxique.
	 */
	private DocumentBuilderFactory factory;

	/**
	 * Pour la création d'un parseur (analyseur syntaxique).
	 */
	private DocumentBuilder builder;

	/**
	 * Pour la création d'un Document (XML).
	 */
	private Document document;

	/**
	 * Attribut de type carte pour créer notre carte, a partir des informations requises.
	 */
	private Carte carte;

	/**
	 * Liste de territoires que l'on va remplir en parcourant notre fichier XML.
	 */
	private ArrayList<Territoire> listTerritoires;

	/**
	 * Liste de noeuds presents dans chaque territoire que l'on va remplir en parcourant notre fichier XML.
	 */
	private ArrayList<Noeud> listNoeuds;
	
	/**
	 * Liste de noeuds presents dans l'ensemble des territoires que l'on va remplir en parcourant notre fichier XML.
	 */
	private ArrayList<Noeud> listNoeudsTotaux;

	/**
	 * Liste des noeuds voisins pour chaque noeud, que l'on va remplir en parcourant notre fichier XML.
	 */
	private ArrayList<String> listNoeudsVoisins;

	/**
	 * Constructeur par defaut.
	 */
	public LireXML()
	{
		//Récupération d'une instance de la classe "DocumentBuilderFactory"
		factory = DocumentBuilderFactory.newInstance();

		try
		{
			//Creation du parseur
			builder = factory.newDocumentBuilder();

			//Creation du document
			document= builder.parse(new File("carte.XML"));
		}
		catch (final ParserConfigurationException e) 
		{
			e.printStackTrace();
		}
		catch (final SAXException e) 
		{
			e.printStackTrace();
		}
		catch (final IOException e) 
		{
			e.printStackTrace();
		}

		//On instancie notre liste de territoires des la creation de l'objet, car il n'y aura qu'une seule liste de Territoires
		listTerritoires = new ArrayList<Territoire>();
		
		//Instanciation de la liste qui contiendra l'ensemble des noeuds de tous les territoires
		listNoeudsTotaux = new ArrayList<Noeud>();
	}

	/**
	 * Methode pour parcourir l'ensemble de la carte depuis le fichier XML.
	 * En image LA STRUCTURE XML
	 * <img src="data:image/png;base64,"/>
	 */
	public void lireCarteXML()
	{
		//Pour la récupération de l'Element racine, qui est l'Element carte (VOIR structure du XML)
		final Element racine = document.getDocumentElement();

		//Lecture des territoires
		lireTerritoireXML(racine);
	}

	/**
	 * Methode pour lire les territoires depuis le fichier XML, et depuis la racine de ce fichier.
	 * @param racine La racine du fichier XML, cad l'Element Carte.
	 */
	public void lireTerritoireXML(Element racine)
	{	
		//Pour recuperer les Elements enfants de premier niveau (cad pour récupérer tous les noeuds enfants de la racine)
		//On recupere donc les territoires
		final NodeList elementTerritoire = racine.getChildNodes();

		//On recupere le nombre de territoires
		final int nbTerritoires = elementTerritoire.getLength();

		//On parcourt la liste des territoires
		for (int i = 0; i<nbTerritoires; i++) 
		{
			//Si le noeud est un ELEMENT (donc pas un commentaire ...)
			if(elementTerritoire.item(i).getNodeType() == Node.ELEMENT_NODE) 
			{
				//On recupere l'Element territoire d'indice i
				//=>pas accessible depuis la classe Node => on "caste" notre noeud en un Element pour pouvoir l'appeler
				final Element territoire = (Element) elementTerritoire.item(i);

				//On recupere  le texte de l'attribut id de l'Element territoire (VOIR structure du XML)
				//getAttribute(nom) renvoie la valeur de l'attribut spécifié en paramètre
				int idTerritoire = Integer.parseInt(territoire.getAttribute("id"));

				//On instancie une liste de noeuds, pour chaque territoire
				listNoeuds = new ArrayList<Noeud>();

				//Pour chaque territoire, lecture des noeuds
				lireNoeudXML(territoire);

				//On construit notre territoire, pour chaque territoire lu
				listTerritoires.add(new Territoire(idTerritoire, listNoeuds));
			}
		}
		//On construit notre carte, apres avoir lu tous les territoires
		carte=new Carte(listTerritoires, this.listNoeudsTotaux);
	}

	/**
	 * Methode pour lire les noeuds depuis le fichier XML, cette lecture se faisant pour chaque Territoire.
	 * @param territoire L'Element Territoire sur le quel on se situe.
	 */
	public void lireNoeudXML(Element territoire)
	{
		//Récupération des noeuds 'noeud' dans territoire (VOIR structure du XML)
		final NodeList elementNoeud = territoire.getElementsByTagName("noeud");

		//Nombre de noeuds 'noeud' 
		final int nbNoeud = elementNoeud.getLength();

		//Pour chaque noeud, on parcourt le noeud
		for(int j = 0; j<nbNoeud; j++) 
		{
			//Récupération du x et y avec pour item(j) => le noeud que l'on parcourt
			final Element x = (Element) territoire.getElementsByTagName("x").item(j);
			final Element y = (Element) territoire.getElementsByTagName("y").item(j);

			//On recupere l'Element noeudsVoisin du nom de 'noeudsVoisins', qui est dans ce territoire (VOIR structure du XML)
			final Element noeudsVoisins = (Element) territoire.getElementsByTagName("noeudsVoisins").item(j);

			//Variables locales pour recuperer le texte des coordonnées
			int xParam, yParam;

			//Recuperation des coordonnées depuis les Elements 'noeud', avec convertion du texte recupere en entier
			xParam=Integer.parseInt(x.getTextContent());
			yParam=Integer.parseInt(y.getTextContent());

			//Récupération des noeuds 'id' dans noeudsVoisins (VOIR structure du XML)
			final NodeList elementId = noeudsVoisins.getElementsByTagName("id");

			//Nombre de noeuds 'noeud' 
			final int nbIdNoeudsVoisins = elementId.getLength();

			//On instancie une liste de noeudsVoisins avant de parcourir chaque noeud voisin, pour chaque noeud
			listNoeudsVoisins = new ArrayList<String>();

			//Pour chaque Element noeudsVoisins
			for(int k = 0; k<nbIdNoeudsVoisins; k++)
			{
				//On recupere l'Element noeudsVoisin du nom de 'noeudsVoisins', d'indice k, qui est dans ce noeudsVoisins
				final Element id = (Element) noeudsVoisins.getElementsByTagName("id").item(k);

				//on remplit notre liste d'id voisins pour chaque noeud
				listNoeudsVoisins.add(id.getTextContent());
			}

			//On construit notre noeud, pour chaque noeud lu
			this.listNoeuds.add(new Noeud(xParam, yParam, listNoeudsVoisins));
			
			//On remplit notre liste qui contiendra l'ensembles des noeuds de tous les territoires
			this.listNoeudsTotaux.add(new Noeud(xParam, yParam, listNoeudsVoisins));
		}
	}
}
